INSERT INTO `wp_posts` VALUES("262", "1", "2019-10-30 18:46:20", "2019-10-30 22:46:20", "<!-- wp:paragraph -->\n<p>Dishlist is a Digital Menu that helps people to better understand restaurants\' menus by presenting several details about each dish such as:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Description</li><li>Ingredients</li><li>Characteristics (e.g. spicy, vegan, gluten-free...)</li><li>Photos</li><li>Price</li><li>Ratings &amp; review</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>Still, the application is a great tool for restaurants to have direct feedback from their customers about each dish.</p>\n<!-- /wp:paragraph -->", "What is Dishlist?", "", "inherit", "closed", "closed", "", "163-revision-v1", "", "", "2019-10-30 18:46:20", "2019-10-30 22:46:20", "", "163", "http://localhost:8000/163-revision-v1/", "0", "revision", "", "0");
