INSERT INTO `wp_posts` VALUES("265", "1", "2019-10-30 19:15:55", "2019-10-30 23:15:55", "<!-- wp:paragraph -->\n<p>You\'ll find a wide range of restaurants that are participants in Dishlist, from different nationalities and with various specialties. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>To know if a specific restaurant is participating you can check whether the restaurant presents the QR code on its table or you can search the restaurant by its name - you can find both functions in the first screen of the app.</p>\n<!-- /wp:paragraph -->", "Which restaurants use Dishlist?", "", "inherit", "closed", "closed", "", "167-revision-v1", "", "", "2019-10-30 19:15:55", "2019-10-30 23:15:55", "", "167", "http://localhost:8000/uncategorized/167-revision-v1/", "0", "revision", "", "0");
